import React from "react";

import {useEffect} from "react";
import SetColors from "./SetColors";

export default function ViewGradient({listColors, angle, setGradient, gradient, setlistColors, colorRandomizer}) {


    useEffect(() => {
        setGradient('linear-gradient('+angle+'deg, '+ listColors[0].code + ', '+ listColors[1].code + ', '+ listColors[2].code + ')')
        document.body.style.backgroundColor = gradient;
        console.log(gradient);
    });

    return (
        <>
            <br />
            <div className="mx-auto" style={{height: "35rem", width: "95%",background: gradient, border:"5px solid black", borderRadius:"25%"}}>

                <div className="app-conainer d-flex align-items-center justify-content-around flex-column text-center">
                    <SetColors key={listColors[0].id} color={listColors[0]} listColors={listColors} setlistColors={setlistColors} id={listColors[0].id}/>
                    <SetColors key={listColors[1].id} color={listColors[1]} listColors={listColors} setlistColors={setlistColors} id={listColors[1].id}/>
                    <SetColors key={listColors[2].id} color={listColors[2]} listColors={listColors} setlistColors={setlistColors} id={listColors[2].id}/>
                    {/*<input type="number" value={angle} onChange={e => {setAngle(e)}}></input>*/}
                </div>

                <button className="btn btn-primary" onClick={colorRandomizer}>Randomizer</button>

                <h3 className="text-center">{gradient}</h3>

            </div>
            <br />
        </>
    )
}
