import {useEffect, useState} from "react";

export default function Limiter({cards}) {

    const [count, setCount] = useState(0);
    function alertBox() {
        alert("oh wtf guy, there is too many tasks. Please take care of them first ! xoxo");
    }

    setCount(cards.filter(e => e.status === false).length);
    useEffect(() => {
        // ce que je veux : map cards -> count++ si status === 0. Si count > 10 -> STOP
        console.log("length",cards.filter(e => e.status === false).length);
        console.log("count",count);
        console.log(cards);

        return () => {
            if(count >= 9) {
                document.getElementById("addTodo").onclick = alertBox;
            }
        };
    }, [cards]);

    return (
        <></>
    )
}
