export default function SetColors({color,listColors, setlistColors, id}) {
    const changeColor = (e) => {
        color.code = e.target.value;
        let newlist = listColors.map(e => {
            if(e.id == id) {
                return {...e, code:color.code}
            }
            return e;
        })
        setlistColors(newlist);
    }

    return (
        <div>
            <label htmlFor="head">Breackpoint {color.id}</label>
            <input type="color" id="head" name="head" value={color.hexa} onChange={e => (setlistColors(e))}/>
        </div>
    )
}
