export default function Card({id, title, content, status, setCards, cards}) {
    if(status === true) {
        document.getElementById(id).text = "Fait";
    }

    function handleDelete() {
        let newCardList = cards.filter(e => e.id !== id);
        setCards(newCardList);
    }

    function handleStatus() {
        switch(status) {
            case true:
                status = false;
                break;
            case false:
                status = true;
                break;
        }
        let newCardList = cards.map(e => {
            if(e.id === id) {
                return {...e, status: status
                }
            }
            return e;
        });

        setCards(newCardList);
    }

    return (
        <div className="card mb-5 mx-auto" style={{maxWidth:'400px'}}>
            <div className="card-body">
                <h5 className="card-title">{title}</h5>
                <p className="card-text">{content}</p>

                <div className="row">
                    <div className="col">
                        <button className="btn btn-danger" onClick={() => handleDelete()}>Supprimer</button>
                    </div>
                    <div className="col">
                        <button type="submit" className="btn" id={id} onClick={() => handleStatus()}>Done</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
