import React from "react";
import { useState, useEffect } from "react";

import SetGradient from "./SetGradient";
import SetColors from "./SetColors";
import SetAngle from "./SetAngle";
import {generateUniqueID} from "web-vitals/dist/modules/lib/generateUniqueID";

export default function Gradient() {

    const [listColors, setlistColors] = useState([{id:1,code:"#fd5b2d"},{id:2,code:"#4505be"},{id:3,code:"#22c2c3"}]);
    const [angle, setAngle] = useState("200");
    const [gradient, setGradient] = useState('');

    function colorRandomizer(e) {
        e.preventDefault();
        console.log("coucou");
        let id = 1;
        while (id !== 3) {
            let value = "#"+Math.floor(Math.random()*16777215).toString(16);
            const newColor = {
                id: id,
                code: value
            };
            setlistColors(prev => [...prev, newColor]);
            id++;
        }
    }

    return(
        <>
            <br />
            <SetGradient listColors={listColors} angle={angle} setGradient={setGradient} gradient={gradient}/>
            <div className="mx-auto align-middle" style={{width: "95%",background: gradient, border:"5px solid black", borderRadius:"5%", margin:0}}>

                <div className="app-conainer d-flex align-items-center justify-content-around flex-column text-center">
                    <SetColors key={listColors[0].id} color={listColors[0]} listColors={listColors} setlistColors={setlistColors} id={listColors[0].id}/>
                    <SetColors key={listColors[1].id} color={listColors[1]} listColors={listColors} setlistColors={setlistColors} id={listColors[1].id}/>
                    <SetColors key={listColors[2].id} color={listColors[2]} listColors={listColors} setlistColors={setlistColors} id={listColors[2].id}/>
                    <SetAngle angle={angle} setAngle={setAngle}/>
                </div>

                <div className="text-center">
                    <button className="btn btn-warning " onClick={colorRandomizer}>Randomizer</button>
                </div>

                <h3 className="text-center">{gradient}</h3>

            </div>
        </>
    )
}
