import { useState } from "react"

export default function Form ({setCards}) {

    const [formTitle, setFormTitle] = useState('');
    const [formContent, setFormContent] = useState('');

    const handleTitleChange = e => {
        console.log(e.target.value);
        setFormTitle(e.target.value);
    }

    const handleContentChange = e => {
        console.log(e.target.value);
        setFormContent(e.target.value);
    }

    const handleSubmit = event => {
        event.preventDefault();
        setCards(previousState => [...previousState, {title: formTitle, content: formContent}])
    }

    return (
        <form className="m-5 mx-auto" style={{maxWidth: '450px'}} onSubmit={handleSubmit}>
            <h1 className="text-center">Alimentez votre todo !</h1>
            <div className="mb-3">
                <label htmlFor="inputTitle" className="form-label">Nom de la tâche</label>
                <input type="title" className="form-control" id="inputTitle" aria-describedby="titleHelp" onChange={handleTitleChange}/>

            </div>
            <div className="mb-3">
            <label htmlFor="inputContent" className="form-label">Contenu de la tâche</label>
                <input type="content" className="form-control" id="inputContent" aria-describedby="contentHelp" onChange={handleContentChange}/>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    )
}
