import React from "react";

import {useEffect, useState} from "react";

export default function ViewGradient({color1, color2, color3, angle}) {

    const [gradient, setGradient] = useState('');

    useEffect(() => {
        setGradient('linear-gradient('+angle+'deg, '+ color1[0] + ' ' + color1[1] + '%, ' + color2[0] + ' ' + color2[1] + '%, ' + color3[0] + ' ' + color3[1] + '%)')
        console.log(gradient);
    });

    return (
        <>
            <div className="mx-auto border-top-50" style={{height: "18rem", width: "18rem",background: gradient}}/>
            <h3 className="text-center">{gradient}</h3>
        </>
    )
}
