import {useEffect, useState} from "react";

export default function Limiter({cards}) {

    const [count, setCount] = useState(0);

    useEffect(() => {
        // ce que je veux : map cards -> count++ si status === 0. Si count > 10 -> STOP
        if(count < 10) {
            cards.map(e => {
                if (e.status === false) {
                    setCount(prev => [...prev, count + 1]);
                }
            });
        }
        return () => {
            alert("oh wtf guy, there is too many tasks. Please take care of them first ! xoxo");
        };
    }, [cards]);

}
