import {useState} from "react";

export default function Card({id, title, content, status, setCards, cards}) {

    const [cardEdit, setEditedCard] = useState(true);
    const [titleEdit, setEditedTitle] = useState({title});
    const [contentEdit, setEditedContent] = useState({content});

    function handleDelete() {
        let newCardList = cards.filter(e => e.id !== id);
        setCards(newCardList);
    }

    function handleStatus() {
        switch(status) {
            case true:
                status = false;
                break;
            case false:
                status = true;
                break;
        }
        let newCardList = cards.map(e => {
            if(e.id === id) {
                return {...e, status: status
                }
            }
            return e;
        });

        setCards(newCardList);
    }

    function HandleButton() {
        switch (status) {
            case true:
                return <button type="submit" className="btn btn-danger" id="inputStatus" onClick={() => handleStatus()}>Undone</button>;
                break;
            case false:
                return <button type="submit" className="btn btn-success" id="inputStatus" onClick={() => handleStatus()}>Done</button>;
                break;
            default :
                return <></>
        }
    }

    function HandleModifButton() {
        switch (cardEdit) {
            case false:
                return <button className="btn btn-secondary btn-sm" onClick={() => DisplayCardContent()}>Edit</button>;
                break;
            case true:
                return <></>
                break;
        }
    }

    function DisplayCardContent() {
        switch(cardEdit) {
            case false:
                return (
                    <>
                        <h5 className="card-title">{title}</h5>
                        <p className="card-text">{content}</p>
                    </>
                )
            break;
            case true:
                return (
                    <form className="mx-5 mx-auto">
                        <input className="form-control" value={title}/>
                        <input className="form-control" value={content}/>
                    </form>
                )
            break;
        }
    }

    return (
        <div className="card mb-5 mx-auto" style={{maxWidth:'400px', minWidth:'250px'}}>
            <HandleModifButton />
            <div className="card-body">
                <DisplayCardContent />
                <div className="row">
                    <div className="col">
                        <button className="btn btn-danger" onClick={() => handleDelete()}>Delete</button>
                    </div>
                    <div className="col">
                        <HandleButton />
                    </div>
                </div>
            </div>
        </div>
    )
}
