import {useEffect, useState} from "react";

export default function Limiter({cards,count,setCount}) {

    function alertBox() {
        alert("oh wtf guy, there is too many tasks. Please take care of them first ! xoxo");
    }

    useEffect(() => {
        // ce que je veux : map cards -> count++ si status === 0. Si count > 10 -> STOP
        setCount(cards.filter(e => e.status === false).length);
        console.log(count);
        console.log(cards);

        return () => {
            if(count+1 >= 10) {
                document.getElementById("addTodo").onclick = alertBox;
            }
        };
    }, [cards]);

    return (
        <></>
    )
}
