export default function SetColors({colorsList,colorsListComplet, setcolorsList, id}) {
    const changeColor = (e) => {
        colorsList.hexa = e.target.value;
        let newlist = colorsListComplet.map(e => {
            if(e.id == id) {
                return {...item, hexa:colorsList.hexa}
            }
            return e;
        })
        setcolorsList(newlist);
    }

    return (
        <div>
            <label htmlFor="head">Breackpoint {colorsList.id}</label>
            <input type="color" id="head" name="head" value={colorsList.hexa} onChange={e => (changeColor(e))}/>
        </div>
    )
}
