import React from "react";

import {useEffect} from "react";

export default function ViewGradient({listColors, angle, setGradient, gradient}) {


    useEffect(() => {
        setGradient('linear-gradient('+angle+'deg, '+ listColors[0].code + ', '+ listColors[1].code + ', '+ listColors[2].code + ')')
        document.body.style.backgroundColor = gradient;
        console.log(gradient);
    });

    return (
        <>
            <br />
            <div className="mx-auto border-top" style={{height: "25rem", width: "50rem",background: gradient}}/>
            <br />
        </>
    )
}
